// Luma MVP - autogenerated by Cursor
import Foundation
import SwiftUI
import AppKit

/// Right-side AI command panel (Cmd+E toggle). Per-tab chat with history.
///
/// Per SRS F2: Toggled via Cmd+E, dockable and dismissible.
/// Per AGENTS.md: AI is user-invoked only; no background activity.
/// Per SECURITY.md: UI must display what will be sent and require confirmation.
struct CommandSurfaceView: View {
    @Binding var isPresented: Bool
    @Binding var messages: [ChatMessage]
    let webViewWrapper: WebViewWrapper
    let commandRouter: CommandRouter
    let gemini: GeminiClient
    let onActionProposed: (LLMResponse) -> Void

    @State private var inputText: String = ""
    @State private var errorMessage: String? = nil
    @State private var isSending: Bool = false
    @State private var actionProposedMessage: String? = nil

    // Auto-loaded page context
    @State private var pageTitle: String? = nil
    @State private var pageText: String? = nil
    @State private var isLoadingContext: Bool = false
    @State private var contextRefreshTimer: Timer? = nil

    private let darkBg = Color(red: 0.08, green: 0.08, blue: 0.1)
    private let darkBgSecondary = Color(red: 0.12, green: 0.12, blue: 0.15)
    private let accent = Color(red: 0.4, green: 0.6, blue: 1.0)

    var body: some View {
        VStack(spacing: 0) {
            // Header
            HStack {
                Text("AI")
                    .font(.system(size: 14, weight: .semibold))
                    .foregroundColor(.white)
                Spacer()
                Button(action: close) {
                    Image(systemName: "xmark")
                        .font(.system(size: 12, weight: .medium))
                        .foregroundColor(.white.opacity(0.7))
                }
                .buttonStyle(.plain)
                .keyboardShortcut(.escape, modifiers: [])
            }
            .padding(.horizontal, 16)
            .padding(.vertical, 12)
            .background(darkBgSecondary)

            // AI Status row
            aiStatusRow()

            // Page context preview (shows what will be sent)
            contextPreviewRow()

            // Chat history
            ScrollViewReader { proxy in
                ScrollView {
                    LazyVStack(alignment: .leading, spacing: 12) {
                        ForEach(messages) { msg in
                            ChatBubble(message: msg, isUser: msg.role == .user)
                        }
                    }
                    .padding()
                }
                .frame(maxHeight: .infinity)
                .background(darkBg)
                .onChange(of: messages.count) { _, _ in
                    if let last = messages.last {
                        withAnimation(.easeOut(duration: 0.2)) {
                            proxy.scrollTo(last.id, anchor: .bottom)
                        }
                    }
                }
            }

            // Input area
            VStack(spacing: 8) {
                HStack(alignment: .bottom, spacing: 8) {
                    GrowingTextEditor(
                        text: $inputText,
                        placeholder: "Message...",
                        minHeight: 36
                    )
                    .padding(8)
                    .background(darkBgSecondary)
                    .foregroundColor(.white)
                    .cornerRadius(10)

                    Button(action: sendCommand) {
                        Image(systemName: "arrow.up.circle.fill")
                            .font(.system(size: 28))
                            .foregroundColor(inputText.isEmpty ? .white.opacity(0.3) : accent)
                    }
                    .buttonStyle(.plain)
                    .disabled(inputText.isEmpty || isSending)
                    .keyboardShortcut(.return, modifiers: .command)
                }

                HStack {
                    if let msg = actionProposedMessage {
                        Text(msg)
                            .font(.caption2)
                            .foregroundColor(.green.opacity(0.9))
                    }

                    if let err = errorMessage {
                        Text(err)
                            .font(.caption2)
                            .foregroundColor(.red.opacity(0.9))
                            .lineLimit(2)
                    }

                    Spacer()
                }
            }
            .padding(12)
            .background(darkBgSecondary)
        }
        .background(darkBg)
        .foregroundColor(.white)
        .onAppear {
            loadPageContext()
            startContextRefreshTimer()
        }
        .onDisappear {
            stopContextRefreshTimer()
        }
    }

    private func sendIfEnter() {
        if !inputText.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty {
            sendCommand()
        }
    }

    private struct ChatBubble: View {
        let message: ChatMessage
        let isUser: Bool

        var body: some View {
            HStack {
                if isUser { Spacer(minLength: 40) }
                Text(message.text)
                    .font(.system(size: 13))
                    .foregroundColor(isUser ? .white : .white.opacity(0.95))
                    .padding(.horizontal, 12)
                    .padding(.vertical, 8)
                    .background(isUser ? Color(red: 0.25, green: 0.4, blue: 0.7) : Color(red: 0.18, green: 0.18, blue: 0.22))
                    .cornerRadius(12)
                if !isUser { Spacer(minLength: 40) }
            }
        }
    }

    @ViewBuilder
    private func aiStatusRow() -> some View {
        if let err = GeminiClient.lastNetworkError {
            HStack {
                Image(systemName: "exclamationmark.triangle.fill")
                    .foregroundColor(.red.opacity(0.9))
                Text("Error â€” \(err)")
                    .font(.caption2)
                    .foregroundColor(.red.opacity(0.9))
                    .lineLimit(2)
                Spacer()
                SettingsLink { Text("Troubleshoot").font(.caption2) }
                    .buttonStyle(.bordered)
            }
            .padding(.horizontal, 12)
            .padding(.vertical, 8)
            .background(Color.red.opacity(0.15))
        } else if KeychainManager.shared.fetchGeminiKey() == nil {
            HStack {
                Image(systemName: "key.fill")
                    .foregroundColor(.orange.opacity(0.9))
                Text("No API Key")
                    .font(.caption2)
                    .foregroundColor(.orange.opacity(0.9))
                Spacer()
                SettingsLink { Text("Settings").font(.caption2) }
                    .buttonStyle(.bordered)
            }
            .padding(.horizontal, 12)
            .padding(.vertical, 8)
            .background(Color.orange.opacity(0.15))
        }
    }

    private func close() {
        stopContextRefreshTimer()
        isPresented = false
        inputText = ""
        errorMessage = nil
        isSending = false
        actionProposedMessage = nil
    }

    /// Starts a timer to refresh context every 3 seconds.
    private func startContextRefreshTimer() {
        stopContextRefreshTimer()
        contextRefreshTimer = Timer.scheduledTimer(withTimeInterval: 3.0, repeats: true) { _ in
            loadPageContext()
        }
    }

    /// Stops the context refresh timer.
    private func stopContextRefreshTimer() {
        contextRefreshTimer?.invalidate()
        contextRefreshTimer = nil
    }

    /// Loads page context (title + visible text) when panel appears.
    private func loadPageContext() {
        isLoadingContext = true
        let group = DispatchGroup()

        group.enter()
        webViewWrapper.evaluatePageTitle { title in
            pageTitle = title
            group.leave()
        }

        group.enter()
        webViewWrapper.evaluateVisibleText(maxChars: 4000) { text in
            pageText = text
            group.leave()
        }

        group.notify(queue: .main) {
            isLoadingContext = false
        }
    }

    /// Builds context string from page metadata.
    private func buildContextString() -> String? {
        var parts: [String] = []

        if let url = webViewWrapper.currentURL {
            parts.append("URL: \(url.absoluteString)")
        }

        if let title = pageTitle, !title.isEmpty {
            parts.append("Title: \(title)")
        }

        if let text = pageText, !text.isEmpty {
            // Truncate for display but send full 4k
            parts.append("Page content:\n\(text)")
        }

        return parts.isEmpty ? nil : parts.joined(separator: "\n")
    }

    @ViewBuilder
    private func contextPreviewRow() -> some View {
        VStack(alignment: .leading, spacing: 4) {
            HStack {
                Image(systemName: "doc.text")
                    .font(.system(size: 10))
                    .foregroundColor(.white.opacity(0.5))
                Text("Context")
                    .font(.caption2)
                    .foregroundColor(.white.opacity(0.5))
                Spacer()
                if isLoadingContext {
                    ProgressView()
                        .scaleEffect(0.6)
                }
            }

            if let url = webViewWrapper.currentURL {
                Text(url.host ?? url.absoluteString)
                    .font(.system(size: 11))
                    .foregroundColor(.white.opacity(0.7))
                    .lineLimit(1)
            }

            if let title = pageTitle, !title.isEmpty {
                Text(title)
                    .font(.system(size: 11, weight: .medium))
                    .foregroundColor(.white.opacity(0.8))
                    .lineLimit(1)
            }

            if let text = pageText, !text.isEmpty {
                Text(text.prefix(150) + (text.count > 150 ? "..." : ""))
                    .font(.system(size: 10))
                    .foregroundColor(.white.opacity(0.5))
                    .lineLimit(2)
            }
        }
        .padding(.horizontal, 12)
        .padding(.vertical, 8)
        .background(Color.white.opacity(0.03))
    }

    private func sendCommand() {
        let trimmed = inputText.trimmingCharacters(in: .whitespacesAndNewlines)
        guard !trimmed.isEmpty else { return }

        isSending = true
        errorMessage = nil
        actionProposedMessage = nil

        let userMsg = ChatMessage(role: .user, text: trimmed)
        messages.append(userMsg)
        inputText = ""

        // Always include page context
        let context = buildContextString()
        proceedWithSend(prompt: trimmed, context: context)
    }

    private func proceedWithSend(prompt: String, context: String?) {
        let promptToSend = prompt
        let contextToSend = context

        gemini.generate(prompt: promptToSend, context: contextToSend) { result in
            DispatchQueue.main.async {
                isSending = false

                switch result {
                case .success(let data):
                    if let response = try? JSONDecoder().decode(LLMResponse.self, from: data) {
                        messages.append(ChatMessage(role: .assistant, text: response.text))
                        if response.action != nil {
                            onActionProposed(response)
                            actionProposedMessage = "Action proposed"
                        }
                    } else {
                        errorMessage = "Failed to parse response"
                    }

                case .failure(let error):
                    let msg = error.localizedDescription
                    errorMessage = msg
                    messages.append(ChatMessage(role: .assistant, text: "Error: \(msg)"))
                    actionProposedMessage = nil
                }
            }
        }
    }
}

// MARK: - Growing multiline text editor

private struct TextHeightKey: PreferenceKey {
    static var defaultValue: CGFloat = 0
    static func reduce(value: inout CGFloat, nextValue: () -> CGFloat) {
        value = nextValue()
    }
}

private struct GrowingTextEditor: View {
    @Binding var text: String
    var placeholder: String
    var minHeight: CGFloat

    /// Height from content; no upper bound so the whole query is visible.
    @State private var contentHeight: CGFloat = 36

    private let font = Font.system(size: 13)

    private var boxHeight: CGFloat {
        max(minHeight, contentHeight)
    }

    var body: some View {
        ZStack(alignment: .topLeading) {
            // Measure content height using invisible text (opacity 0 so no double-text overlay)
            Text(text.isEmpty ? " " : text)
                .font(font)
                .frame(maxWidth: .infinity, alignment: .topLeading)
                .fixedSize(horizontal: false, vertical: true)
                .padding(8)
                .opacity(0)
                .background(
                    GeometryReader { g in
                        Color.clear.preference(key: TextHeightKey.self, value: g.size.height)
                    }
                )
                .allowsHitTesting(false)

            TextEditor(text: $text)
                .font(font)
                .scrollContentBackground(.hidden)
                .padding(4)
                .frame(minHeight: minHeight, maxHeight: .infinity)
                .frame(height: boxHeight)

            if text.isEmpty {
                Text(placeholder)
                    .font(font)
                    .foregroundColor(.white.opacity(0.4))
                    .padding(12)
                    .frame(maxWidth: .infinity, alignment: .topLeading)
                    .frame(height: boxHeight)
                    .allowsHitTesting(false)
            }
        }
        .frame(height: boxHeight)
        .onPreferenceChange(TextHeightKey.self) { h in
            contentHeight = max(minHeight, h)
        }
    }
}
