private struct PanelResizeHandle: View {
    @Binding var panelWidth: CGFloat

    private let minWidth: CGFloat = 280
    private let maxWidth: CGFloat = 700
    @State private var dragStartWidth: CGFloat = 0

    var body: some View {
        Rectangle()
            .fill(Color.clear)
            .frame(width: 6)
            .contentShape(Rectangle())
            .onHover { hovering in
                if hovering {
                    NSCursor.resizeLeftRight.push()
                } else {
                    NSCursor.pop()
                }
            }
            .gesture(
                DragGesture()
                    .onChanged { value in
                        if dragStartWidth == 0 {
                            dragStartWidth = panelWidth
                        }
                        let newWidth = dragStartWidth - value.translation.width
                        panelWidth = min(maxWidth, max(minWidth, newWidth))
                    }
                    .onEnded { _ in
                        dragStartWidth = 0
                    }
            )
    }
}

// MARK: - Tab strip (titlebar, leading-aligned)

private struct TabStripView: View {
    @ObservedObject var tabManager: TabManager
    let contentAreaColor: Color
    let onSwitch: (UUID) -> Void
    let onClose: (UUID) -> Void
    let onNewTab: () -> Void

    private let rowHeight: CGFloat = 30

    var body: some View {
        GeometryReader { geo in
            let count = tabManager.tabCount()
            let tabWidth: CGFloat = count > 0 ? max(90, (geo.size.width - 44) / CGFloat(count)) : 0

            HStack(spacing: 0) {
                ForEach(Array(tabManager.tabOrder.enumerated()), id: \.element) { index, tabId in
                    TabPill(
                        tabId: tabId,
                        index: index + 1,
                        url: tabManager.tabURL[tabId] ?? nil,
                        isActive: tabManager.currentTab == tabId,
                        contentAreaColor: contentAreaColor,
                        onSelect: { onSwitch(tabId) },
                        onClose: { onClose(tabId) }
                    )
                    .frame(width: tabWidth, height: rowHeight)
                    .id(tabId)
                }

                Button(action: onNewTab) {
                    Image(systemName: "plus")
                        .font(.system(size: 14, weight: .medium))
                        .foregroundStyle(.secondary)
                        .frame(width: 36, height: rowHeight)
                        .contentShape(Rectangle())
                        .background(RoundedRectangle(cornerRadius: 6).fill(Color(white: 0.22).opacity(0.6)))
                }
                .buttonStyle(.plain)
                .accessibilityLabel("New tab")
            }
            .frame(maxWidth: .infinity, alignment: .leading)
        }
        .frame(height: rowHeight)
    }
}

private struct TabPill: View {
    let tabId: UUID
    let index: Int
    let url: URL?
    let isActive: Bool
    let contentAreaColor: Color
    let onSelect: () -> Void
    let onClose: () -> Void

    @State private var isHovered = false

    private var title: String {
        url?.host ?? url?.absoluteString ?? "New Tab"
    }

    private var leadingIcon: String {
        url == nil ? "globe" : "doc.text"
    }

    var body: some View {
        ZStack(alignment: .trailing) {
            // Tab body — clicking switches to this tab
            Button(action: onSelect) {
                HStack(spacing: 6) {
                    Image(systemName: leadingIcon)
                        .font(.system(size: 11, weight: .medium))
                        .foregroundStyle(isActive ? .primary : .secondary)
                        .frame(width: 14, alignment: .center)

                    Text(title)
                        .lineLimit(1)
                        .truncationMode(.tail)
                        .font(.system(size: 12, weight: .medium))
                        .foregroundStyle(isActive ? .primary : .secondary)
                        .frame(maxWidth: .infinity, alignment: .leading)
                }
                .padding(.leading, 8)
                .padding(.trailing, 28)
                .padding(.vertical, 4)
                .frame(maxWidth: .infinity, maxHeight: .infinity)
                .contentShape(Rectangle())
            }
            .buttonStyle(.plain)

            // Close button — always visible, clickable
            Button(action: onClose) {
                Image(systemName: "xmark")
                    .font(.system(size: 9, weight: .semibold))
                    .foregroundStyle(.secondary)
                    .frame(width: 20, height: 20)
                    .contentShape(Rectangle())
            }
            .buttonStyle(.plain)
            .opacity(isHovered || isActive ? 1 : 0.6)
            .padding(.trailing, 6)
            .accessibilityLabel("Close tab")
        }
        .onHover { hovering in
            isHovered = hovering
        }
        .background(
            RoundedRectangle(cornerRadius: 6)
                .fill(isActive
                    ? contentAreaColor
                    : Color(white: 0.22).opacity(isHovered ? 0.9 : 0.6))
        )
        .animation(.easeInOut(duration: 0.15), value: isHovered)
        .animation(.easeInOut(duration: 0.15), value: isActive)
        .accessibilityElement(children: .combine)
        .accessibilityLabel("Tab \(index): \(title)")
        .accessibilityAddTraits(isActive ? [.isButton, .isSelected] : .isButton)
    }
}

struct WebViewContainer: NSViewRepresentable {
    let webView: WKWebView

    func makeNSView(context: Context) -> WKWebView { webView }
    func updateNSView(_ nsView: WKWebView, context: Context) {}
}

